mapper.select:
SELECT * FROM Personal_Contact
WHERE writer = #{writer}
AND (
tag = #{tag}
OR tag LIKE CONCAT('%', #{tag}, '%')
OR tag LIKE
CONCAT('%,', #{tag}, '%')
OR tag LIKE CONCAT('%', #{tag}, ',%')
);

SELECT * FROM Share_Contact
WHERE (
tag = #{tag}
OR tag LIKE
CONCAT('%', #{tag}, '%')
OR tag LIKE CONCAT('%,', #{tag}, '%')
OR tag
LIKE CONCAT('%', #{tag}, ',%')
);

mapper.delete:
DELETE FROM Personal_Contact
WHERE writer = #{writer}
AND (
tag = #{tag}
OR tag LIKE CONCAT('%', #{tag}, '%')
OR tag LIKE
CONCAT('%,', #{tag}, '%')
OR tag LIKE CONCAT('%', #{tag}, ',%')
);

DELETE FROM Share_Contact
WHERE (
tag = #{tag}
OR tag
LIKE CONCAT('%', #{tag}, '%')
OR tag LIKE
CONCAT('%,', #{tag}, '%')
OR
tag LIKE CONCAT('%', #{tag}, ',%')
);
